export function ConvertElementToImage(renderElement,optionsJson=null){let config={};optionsJson!==null&&optionsJson!==""&&(config=JSON.parse(optionsJson));const bufferId=Date.now().toString(18)+Math.random().toString(36).substring(2);return html2canvas(renderElement,config).then(canvas=>{let renderedData=canvas.toDataURL("image/png");renderedData=renderedData.substring(renderedData.indexOf(",")+1);SetLocalStorageItem(bufferId,renderedData)}),bufferId}export function SetLocalStorageItem(key,value){localStorage.setItem(key,value)}export function ClearLocalStorageItem(key){localStorage.setItem(key,null)}export function RemoveLocalStorageItem(key){localStorage.removeItem(key)}export function ReadLocalStorageItem(key){return localStorage.getItem(key)}import"./vendor.min.js";